document.addEventListener("DOMContentLoaded",()=>{const e=document.body;window.addEventListener("scroll",()=>{const n=window.scrollY,t=300,s=0,o=Math.min(Math.max((n-t)/(s-t),0),1);e.style.setProperty("--home-fade-opacity",o)})});var list_config,titles,currentConfig,layouts=["background","hero","profile","page","card"],currentLayout=0;function switchHomeLayout(){var e,t,n=currentLayout;currentLayout=currentLayout==layouts.length-1?0:currentLayout+1,e=document.getElementById(layouts[n]),t=document.getElementById(layouts[currentLayout]);const s=document.querySelectorAll("code[id=layout]");t.style.display="block",e.style.display="none",s.forEach(function(e){e.innerText=layouts[currentLayout]})}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-layout-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchHomeLayout()}))}),list_config=["CardViewProse","CardViewScreenWidth","NormalView"],titles={CardViewProse:"card view with constrained width",CardViewScreenWidth:"card view with full width",NormalView:"standard list view"},currentConfig=0;function switchList(){var e,t,n=currentConfig;currentConfig=currentConfig==list_config.length-1?0:currentConfig+1,e=document.getElementById(list_config[n]),t=document.getElementById(list_config[currentConfig]);const s=document.querySelectorAll("code[id=config]");t.style.display="block",e.style.display="none",s.forEach(function(e){e.innerText=titles[list_config[currentConfig]]})}window.addEventListener("DOMContentLoaded",e=>{document.querySelectorAll("#switch-config-button").forEach(e=>e.addEventListener("click",function(e){e.preventDefault(),switchList()}))})